import React, { useState, useEffect } from "react";
import axios from "axios";

const Home = () => {
	const [serverStatus, setServerStatus] = useState(null);
	const [loading, setLoading] = useState(true);

	useEffect(() => {
		const checkServerStatus = async () => {
			try {
				const response = await axios.get("/api/health");
				setServerStatus(response.data);
				setLoading(false);
			} catch (error) {
				console.error("Failed to connect to server:", error);
				setLoading(false);
			}
		};

		checkServerStatus();
	}, []);

	return (
		<div className="home-container">
			<h1 className="title">Welcome to React + Express</h1>
			<p className="subtitle">A modern full-stack web application</p>

			<div className="card">
				<h2>Server Status</h2>
				{loading ? (
					<p>Checking server connection...</p>
				) : serverStatus ? (
					<div>
						<p>
							<span
								className={`status-indicator ${
									true ? "connected" : "disconnected"
								}`}
							></span>
							Server is running and healthy!
						</p>
						<p>
							<strong>Status:</strong> {serverStatus.status}
						</p>
						<p>
							<strong>Timestamp:</strong>{" "}
							{new Date(serverStatus.timestamp).toLocaleString()}
						</p>
					</div>
				) : (
					<p>
						<span
							className={`status-indicator ${
								false ? "connected" : "disconnected"
							}`}
						></span>
						Unable to connect to server
					</p>
				)}
			</div>

			<div className="card">
				<h2>Features</h2>
				<ul className="features-list">
					<li>React 18 with modern hooks</li>
					<li>Node.js + Express backend</li>
					<li>RESTful API endpoints</li>
					<li>SCSS for styling</li>
					<li>React Router for navigation</li>
					<li>Axios for API calls</li>
					<li>Responsive design</li>
				</ul>
			</div>
		</div>
	);
};

export default Home;
